```c++
struct node{
    int to,cost,next;
}e[MAXN];
int head[MAXN];
int val[MAXN];
int dp[2][MAXN];
int cnt = 0;

void add_edge(int f,int t,int c)
{
    e[cnt].to = t;
    e[cnt].cost = c;
    e[cnt].next = head[f];
    head[f] = cnt;
    cnt++;
}

void dfs(int u,int pre)
{
    dp[0][u] = -val[u];
    dp[1][u] = val[u];
    for(int i=head[u]; i!=-1; i=e[i].next){
        int v = e[i].to;
        int cost = e[i].cost;
        if(pre==v) continue;
        dfs(v,u);
        dp[0][u] = max(dp[0][u],dp[0][v]-cost);
        dp[1][u] = max(dp[1][u],dp[1][v]-cost);
    }
}

int main()
{
    int t,n;
    sc(t);
    while(t--){
        sc(n);
        for(int i=1; i<=n; i++) sc(val[i]);
        memset(head,-1,sizeof(head));
        memset(dp,0,sizeof(dp));
        cnt = 0;
        for(int i=1; i<n; i++){
            int f,t,c;
            sc(f);  sc(t); sc(c);
            add_edge(f,t,c);
            add_edge(t,f,c);
        }
        dfs(1,-1);
        int ans = 0;
        for(int i=1; i<=n; i++){
            ans = max(ans,dp[0][i]+dp[1][i]);
        }
        printf("%d\n",ans);
    }
}
```

