struct dfs{
    int v,pre,d,i;
    dfs(int vv,int pp,int dd,int ii):v(vv),pre(pp),d(dd),i(ii){}
};
stack<dfs> s;

void solve(int &k){
    s.push(dfs(1,0,0,0));
    int v,pre,d,i;
    while(!s.empty()){
        dfs cur = s.top();
        s.pop();
        //cout<<s.size()<<endl;
        v = cur.v;  pre = cur.pre; d = cur.d; i = cur.i;
        if(i==0){
            pos[v] = k;
            vs[k] = v;
            depth[k++] = d;
